import Hello from './Hello'
// Hello 컴포넌트를 가져온다 (경로 지정)

function App() {
  const name = 'react';
  return (
    <>
      {/* 컴포넌트는 여러 번 재사용이 가능하다. */}
      {/* JSX의 태그는 항상 닫아주어야 하며, </> <- 이런식으로 태그 하나로 요약 가능하다. */}
      <Hello />
      <Hello />
      <Hello />
      {/* JSX 내에 JS 변수를 보일 떄는 {}로 감싼다 */}
      <div>{name}</div>
    </>
  );
}

export default App;
