import {useReducer, useRef, useMemo, useCallback} from 'react';
import UserList from './UserList';
import CreateUser from './CreateUser';

function countActiveUsers(users) {
  console.log('활성 사용자 수를 세는중...');
  return users.filter(user => user.active).length;
}

const initialize = {
  inputs: {
    username: '',
    email: ''
  },
  users: [
    {
      id: 1,
      username: 'velopert',
      email: 'public.velopert@gmail.com',
      active: true
   },
   {
      id: 2,
      username: 'tester',
      email: 'tester@example.com',
      active: false
    },
    {
      id: 3,
      username: 'liz',
      email: 'liz@example.com',
      active: false
   }
  ]
};

function reducer(state, action) {
  switch(action.type) {
    case "CHANGE_INPUT":
      return {
        ...state,
        inputs: {
          ...state.inputs,
          [action.name]: action.value
          //state.inputs.[action.name] => state.inputs[usename/email]
        }
      };
    case "CREATE_USER":
      return {
        users: [...state.users, action.user],
        inputs: {
          ...state.inputs,
          username: '',
          email: ''
        }
      };
    default:
      return state;
  };
}


function App() {
  const [state, dispatch] = useReducer(reducer, initialize);
  const {users} = state;
  const nextId = useRef(4);
  const {username, email} = inputs;

  const onChange = useCallback(e => {
    const {name, value} = e.target;
    dispatch({
      type: "CHANGE_INPUT",
      name,
      value
    });
  }, []);

  const onCreate = useCallback(() => {
    dispatch({
      type: "CREATE_USER",
      user: {
        id: nextId.current,
        username,
        email
      }
    });
    nextId.current += 1;
  }, []);

  const onRemove = useCallback(id => {
    // Array.prototype.filter()를 이용하여
    // 테스트를 통과하는 배열의 요소만 쭈압쭈압 뽑아서 새 배열로 반환함
    setUsers(users => (users.filter(user => user.id !== id)));
  }, []
  );

  // 해당 id와 맞는 요소의 user.active 값을 반전시킨 배열을 반환하여 상태를 바꾼다.
  const onToggle = useCallback(id => {
    setUsers(users => (
      users.map(user =>
        user.id === id ? {...user, active: !user.active} : user
      )
    ));
  }, []
  );

  const count = useMemo(() => countActiveUsers(users), [users]);

  return (
    <>
      <CreateUser 
        onChange={onChange}
        onCreate={onCreate}
        username={username}
        email={email}
      />
      <UserList users={users} onRemove={onRemove} onToggle={onToggle}/>
      <div>활성사용자수 = {count}</div>
    </>
  );
}

export default App;
